mapper.select:

SELECT     


   FROM clearing_profit_record cpr
     WHERE     cpr.trans_no = #{transNo,jdbcType=VARCHAR}
           and cpr.profit_status = #{profitStatus,jdbcType=VARCHAR}
           and cpr.profit_bath = #{profitBath,jdbcType=VARCHAR}

mapper.update:
update clearing_profit_record cpr 
       set cpr.profit_bath = #{profitBath,jdbcType=VARCHAR}, 
        cpr.profit_time = #{profitTime,jdbcType=TIMESTAMP},
           cpr.profit_status = #{settleProfitStatus,jdbcType=VARCHAR}                                                     
       where cpr.trans_no = #{transNo,jdbcType=VARCHAR}  
             and cpr.profit_status = #{profitStatus,jdbcType=VARCHAR}
