mapper.update:
	UPDATE 
	  settle_channel_record
  SET 
    settle_status=#{settleStatus,jdbcType=CHAR}
  WHERE 
    check_status=#{checkStatus,jdbcType=CHAR} and settle_bath=#{settleBath,jdbcType=CHAR}

  	UPDATE
		settle_channel_record
	SET
		settle_status=#{settleStatus}
	WHERE 
		settle_bath=#{settleBath}

mapper.select:
	select   

   from settle_channel_record t
 where t.check_status = #{checkStatus,jdbcType=VARCHAR}
 	and t.settle_status != #{settleStatus,jdbcType=VARCHAR}
 	and t.settle_time = #{settleTime,jdbcType=VARCHAR}

	select   

   from settle_channel_record 
 where channel_code = #{channelCode,jdbcType=VARCHAR}
 	and channel_type = #{channelType,jdbcType=VARCHAR}
 	and settle_time = #{settleTime,jdbcType=VARCHAR}
 	and settle_status= #{settleStatus,jdbcType=VARCHAR}
 	and settle_bath= #{settleBath,jdbcType=VARCHAR}
