mapper.select:
SELECT 

FROM merchant_transfer a

WHERE a.transfer_id = #{transferId}

SELECT 

FROM merchant_transfer a




		AND a.transfer_id = #{transferId}


		AND a.creator = #{creator}


		AND a.auditor = #{auditor}


		AND a.d_merchant_id = #{dMerchantId}


		AND a.d_account_id = #{dAccountId}


		AND a.d_account_name = #{dAccountName}


		AND a.c_merchant_id = #{cMerchantId}


		AND a.c_account_id = #{cAccountId}


		AND a.c_account_name = #{cAccountName}


		AND a.op_resource = #{opResource}


		AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d 00:00:00') AND date_format(#{endCreateTime},'%Y-%m-%d 23:59:59')




		ORDER BY ${page.orderBy}

SELECT 

FROM merchant_transfer a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE merchant_transfer SET
status = #{status},
auditor = #{auditor},
audit_time = #{auditTime},
audit_remark = #{auditRemark}
WHERE transfer_id = #{transferId}
