mapper.select:
SELECT 

FROM adjust_account a

WHERE a.adjust_id = #{adjustId}

SELECT 

FROM adjust_account a



		AND a.serial_no = #{serialNo}


		AND a.status = #{status}


		AND a.type = #{type}


		AND a.creator = #{creator}


		AND a.auditor = #{auditor}


		AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')



	ORDER BY createTime ASC


	ORDER BY createTime DESC



		ORDER BY ${page.orderBy}

SELECT 

FROM adjust_account a






		ORDER BY ${page.orderBy}

		select max(serial_no) as serialNo from adjust_account
		where create_time >= #{beginDate} 
 	    and create_time <= #{endDate}
