mapper.select:
SELECT 

FROM differ_record a

WHERE a.differ_id = #{id}

SELECT 

FROM differ_record a




		AND a.payment_id = #{paymentId}


		AND a.trans_no = #{transNo}


		AND a.merchant_id = #{merchantId}


		AND a.merchant_company = #{merchantCompany}


		AND a.trans_status = #{transStatus}


		AND a.trans_type = #{transType}


		AND a.pay_type = #{payType}


		AND a.product_code = #{productCode}


		AND a.trans_pay_time BETWEEN #{beginTransPayTime} AND #{endTransPayTime}


		AND a.bank_serial_no = #{bankSerialNo}


		AND a.differ_type = #{differType}


		AND a.differ_status = #{differStatus}


		AND a.operate_source = #{operateSource}


		AND a.operate_time BETWEEN #{beginOperateTime} AND #{endOperateTime}




		ORDER BY ${page.orderBy}

SELECT 

FROM differ_record a






		ORDER BY ${page.orderBy}
