mapper.select:
SELECT 

FROM hgms_batch_pay_record_history a

WHERE a.id = #{id}

SELECT 

FROM hgms_batch_pay_record_history a




		AND a.batch_id = #{batchId}



		AND a.merchant_id = #{merchantId}


		AND a.merchant_company LIKE  CONCAT(CONCAT('%', #{merchantCompany}),'%')


		AND a.create_time BETWEEN #{beginCreateTime} AND #{endCreateTime}


		AND a.batch_file_name LIKE  CONCAT(CONCAT('%', #{batchFileName}),'%')




		ORDER BY ${page.orderBy}

SELECT 

FROM hgms_batch_pay_record_history a






		ORDER BY ${page.orderBy}
