mapper.select:
SELECT

FROM merchant_bank_card_authentication a

WHERE a.id = #{id}

SELECT

FROM merchant_bank_card_authentication a



		AND a.merchant_id = #{merchantId}


		AND a.merchant_company_name LIKE
		'%'||#{merchantCompanyName}||'%'
		'%'+#{merchantCompanyName}+'%'
		concat('%',#{merchantCompanyName},'%')


		AND a.pay_status = #{payStatus}


		AND a.authentication_status = #{authenticationStatus}


		AND a.create_time BETWEEN #{beginCreateTime} AND
		#{endCreateTime}




		ORDER BY ${page.orderBy}

SELECT

FROM merchant_bank_card_authentication a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE merchant_bank_card_authentication
SET
authentication_status = #{authenticationStatus},
authentication_amount = #{authenticationAmount}
WHERE merchant_id =
#{merchantId}
