mapper.select:
SELECT
FROM settle_cycle_manage a left join merchant b on a.merchant_id = b.user_id
WHERE a.id = #{id}

SELECT

FROM settle_cycle_manage a
left join merchant b 
on a.merchant_id = b.user_id 

WHERE a.merchant_id = #{merchantId}
AND a.product_code = #{productCode}

	AND a.id != #{id}

SELECT
FROM settle_cycle_manage a left join merchant b on a.merchant_id = b.user_id
WHERE a.merchant_id = #{merchantId}

SELECT

FROM settle_cycle_manage a
left join merchant b 
on a.merchant_id = b.user_id 



		AND a.merchant_id = #{merchantId}


		AND b.email = #{merchantLoginName}


		AND a.settle_type = #{settleType}


		AND a.effective_start_time >= #{effectiveStartTime}


		AND a.effective_end_time <= #{effectiveEndTime}




		ORDER BY ${page.orderBy}

SELECT
FROM settle_cycle_manage a left join merchant b on a.merchant_id = b.user_id
ORDER BY ${page.orderBy}

mapper.update:
UPDATE settle_cycle_manage SET
status = #{status}
WHERE id = #{id}
