mapper.select:
SELECT 

FROM batch_collection_record a

WHERE a.batch_id = #{id}

SELECT 

FROM batch_collection_record a




		AND a.batch_id = #{batchId}


		AND a.account_id = #{accountId}


		AND a.merchant_id = #{merchantId}


		AND a.merchant_login_name = #{merchantLoginName}


		AND a.account_name = #{accountName}


		AND a.total_amount = #{totalAmount}


		AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')




			AND a.status = 'AUDING' OR a.status = 'PROCES'


			AND a.status = #{status}









		ORDER BY ${page.orderBy}

SELECT 

FROM batch_collection_record a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE batch_collection_record set
	status = #{status}
WHERE batch_id = #{batchId}
