mapper.select:
SELECT 

FROM batch_collection_record_detail a

WHERE a.id = #{id}

		SELECT 

		FROM batch_collection_record_detail a



				AND a.merchant_id = #{merchantId}


				AND a.merchant_user_id = #{merchantUserId}



					AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')


					AND a.update_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')



				AND a.bankcard_owner_mobile = #{bankcardOwnerMobile}


				AND a.bankcard_owner_name = #{bankcardOwnerName}


				AND a.bankcard_owner_idcard = #{bankcardOwnerIdcard}


				AND a.merchant_batch_no = #{merchantBatchNo}


				AND a.merchant_order_no = #{merchantOrderNo}


				AND a.status = #{status}


        ORDER BY create_time desc

		SELECT 

		FROM batch_collection_record_detail a



				AND merchant_id=#{merchantId}


				AND merchant_user_id=#{merchantUserId}


				AND merchant_batch_no=#{merchantBatchNo}


				AND merchant_order_no=#{merchantOrderNo}


        ORDER BY create_time desc
