mapper.select:
SELECT 

FROM batch_pay_record a

WHERE a.batch_id = #{id}

		SELECT 

		FROM batch_pay_record a




				AND a.merchant_login_name = #{merchantLoginName}


				AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')


				AND a.bank_order_id = #{bankOrderId}


				AND a.batch_id = #{batchId}


				AND a.merchant_batch_no = #{merchantBatchNo}


				AND a.merchant_id = #{merchantId}


                AND a.status in('PROCES','PENDNG')


                AND a.status in('PROCES','FINISH','PENDNG')



			ORDER BY createTime ASC


			ORDER BY createTime DESC



				ORDER BY ${page.orderBy}

select from batch_pay_record a
AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')
AND a.batch_id = #{batchId}
AND a.merchant_batch_no = #{merchantBatchNo}
AND a.merchant_id = #{merchantId}
and a.status = 'PROCES' and exists (select 1 from batch_pay_record_detail b where b.batch_id = a.batch_id and b.status = 'PREAUD' and b.exception_status = 'NORMAL')
ORDER BY createTime ASC
ORDER BY createTime DESC

SELECT 

FROM batch_pay_record a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE batch_pay_record set
status = #{status}
WHERE batch_id = #{batchId}
