mapper.select:
SELECT 

FROM charge_record a

WHERE a.charge_id = #{id}

	SELECT  

	FROM charge_record a




			AND a.charge_id = #{chargeId}


			AND a.merchant_id = #{merchantId}


			AND a.merchant_company = #{merchantCompany}


			AND a.merchant_login_name = #{merchantLoginName}


			AND a.account_id = #{accountId}


			AND a.account_name LIKE 
				'%'||#{accountName}||'%'
				'%'+#{accountName}+'%'
				concat('%',#{accountName},'%')


			AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')


			AND a.status = #{status}


			AND a.pay_type = #{payType}


			AND a.merchant_id in

				#{merchantIds[${index}]}




            ORDER BY createDate ASC


            ORDER BY createDate DESC

SELECT 

FROM charge_record a






		ORDER BY ${page.orderBy}

mapper.update:
update  charge_record set 
status='CAGING', update_time = now()
WHERE charge_id = #{id}
