mapper.select:
SELECT 

FROM quant_group_pay_record a

WHERE a.id = #{id}

SELECT 

FROM quant_group_pay_record a



		AND a.quant_id = #{quantId}


		AND a.merchant_id = #{merchantId}


		AND a.phone_no LIKE
			'%'||#{phoneno}||'%'
			'%'+#{phoneno}+'%'
			concat('%',#{phoneno},'%')


		AND a.order_amount BETWEEN #{beginOrderAmount} AND #{endOrderAmount}


		AND a.term = #{term}


		AND a.pay_mode = #{payMode}


		AND a.ext_data = #{extData}



			AND a.create_time BETWEEN #{beginCreateTime} AND #{endCreateTime}


			AND a.update_time BETWEEN #{beginCreateTime} AND #{endCreateTime}



		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_time desc

SELECT 

FROM quant_group_pay_record a






		ORDER BY ${page.orderBy}
