mapper.select:
SELECT 

FROM statistics_channel_record a

WHERE a.id = #{id}

SELECT a.channel_code AS "channelCode", a.channel_partners AS "channelPartners", a.bank_name AS "bankName", a.bank_type AS "bankType", a.channel_partners_name AS "channelPartnersName", a.channel_type AS "channelType", a.bank_code AS "bankCode", SUM(a.total_count) AS "totalCount", SUM(a.total_amount) AS "totalAmount", SUM(a.sucess_count) AS "sucessCount", a.channel_type_name AS "channelTypeName", SUM(a.sucess_amount) AS "sucessAmount", SUM(a.fee_amount) AS "feeAmount", SUM(a.fail_count) AS "failCount", SUM(a.fail_amount) AS "failAmount" FROM statistics_channel_record a
AND a.channel_partners = #{channelPartners}
AND a.channel_type = #{channelType}
AND a.bank_code = #{bankCode}
AND a.bank_type = #{bankType}
AND a.statistic_time BETWEEN #{beginStatisticTime} AND #{endStatisticTime}
AND a.statistic_time = #{statisticTime} AND a.sucess_count > 0
AND a.channel_code = #{channelCode}
AND a.statistic_hour is NULL
GROUP BY channel_code,bank_code,channel_type,channel_partners,channel_partners_name,bank_name,channel_type_name,bank_type

SELECT 

FROM statistics_channel_record a




		AND a.channel_partners = #{channelPartners}


		AND a.channel_type = #{channelType}


		AND a.bank_code = #{bankCode}


		AND a.statistic_time BETWEEN #{beginStatisticTime} AND #{endStatisticTime}


		AND a.statistic_time = #{statisticTime} AND a.sucess_count > 0


		AND a.channel_code = #{channelCode}


	ORDER BY 
		statistic_time desc,statistic_hour desc

SELECT a.statistic_hour AS "statisticHour", SUM(a.total_count) AS "totalCount", SUM(a.total_amount) AS "totalAmount", SUM(a.sucess_count) AS "sucessCount", SUM(a.sucess_amount) AS "sucessAmount", SUM(a.fee_amount) AS "feeAmount", SUM(a.fail_count) AS "failCount", SUM(a.fail_amount) AS "failAmount" FROM statistics_channel_record a
AND a.channel_partners = #{channelPartners}
AND a.channel_type = #{channelType}
AND a.bank_code = #{bankCode}
AND a.statistic_time BETWEEN #{beginStatisticTime} AND #{endStatisticTime}
AND a.statistic_time = #{statisticTime} AND a.sucess_count > 0
AND a.statistic_hour >=0
GROUP BY statistic_hour

SELECT a.statistic_time AS "statisticTime", SUM(a.total_count) AS "totalCount", SUM(a.total_amount) AS "totalAmount", SUM(a.sucess_count) AS "sucessCount", SUM(a.sucess_amount) AS "sucessAmount", SUM(a.fee_amount) AS "feeAmount", SUM(a.fail_count) AS "failCount", SUM(a.fail_amount) AS "failAmount" FROM statistics_channel_record a
AND a.channel_partners = #{channelPartners}
AND a.channel_type = #{channelType}
AND a.bank_code = #{bankCode}
AND a.statistic_time BETWEEN #{beginStatisticTime} AND #{endStatisticTime}
AND a.statistic_time = #{statisticTime} AND a.sucess_count > 0
GROUP BY statistic_time

SELECT SUM(a.total_count) AS "totalCount", SUM(a.total_amount) AS "totalAmount", SUM(a.sucess_count) AS "sucessCount", SUM(a.sucess_amount) AS "sucessAmount", SUM(a.fee_amount) AS "feeAmount", SUM(a.fail_count) AS "failCount", SUM(a.fail_amount) AS "failAmount" FROM statistics_channel_record a
AND a.channel_partners = #{channelPartners}
AND a.channel_type = #{channelType}
AND a.bank_code = #{bankCode}
AND a.statistic_time BETWEEN #{beginStatisticTime} AND #{endStatisticTime}
AND a.statistic_time = #{statisticTime} AND a.sucess_count > 0

SELECT 

FROM statistics_channel_record a






		ORDER BY ${page.orderBy}
