mapper.select:
SELECT 

FROM statistics_record_perhour a

WHERE a.id = #{id}

SELECT 
	a.pay_type AS "payType",
	a.statistic_hour AS "statisticHour",
	sum(a.sucess_count) AS "sucessCount",
	sum(a.sucess_amount) AS "sucessAmount",
	sum(a.fee_amount) AS "feeAmount",
	sum(a.settle_amount) AS "settleAmount"
FROM statistics_record_perhour a



		AND a.merchant_id = #{merchantId}



		AND a.merchant_login_name = #{merchantLoginName}


		AND a.allow_system = #{allowSystem}


		AND a.trans_type = #{transType}


		AND a.product_code = #{productCode}


		AND a.pay_type = #{payType}


		AND a.channel_code = #{channelCode}


		AND a.channel_partners = #{channelPartners}


		AND a.channel_partners_name = #{channelPartnersName}


		AND a.channel_type = #{channelType}


		AND a.channel_type_name = #{channelTypeName}


		AND a.bank_code = #{bankCode}


		AND a.bank_name = #{bankName}


		AND a.bank_type = #{bankType}


		AND a.statistics_time BETWEEN #{beginStatisticsTime} AND #{endStatisticsTime}


	GROUP BY 
		payType,statisticHour


		ORDER BY ${page.orderBy}

SELECT 

FROM statistics_record_perhour a




		AND a.merchant_id = #{merchantId}



		AND a.merchant_login_name = #{merchantLoginName}


		AND a.allow_system = #{allowSystem}


		AND a.trans_type = #{transType}


		AND a.product_code = #{productCode}


		AND a.pay_type = #{payType}


		AND a.channel_code = #{channelCode}


		AND a.channel_partners = #{channelPartners}


		AND a.channel_partners_name = #{channelPartnersName}


		AND a.channel_type = #{channelType}


		AND a.channel_type_name = #{channelTypeName}


		AND a.bank_code = #{bankCode}


		AND a.bank_name = #{bankName}


		AND a.bank_type = #{bankType}


		AND a.statistics_time BETWEEN #{beginStatisticsTime} AND #{endStatisticsTime}


		AND a.statistics_time = #{statisticsTime}




		ORDER BY ${page.orderBy}

SELECT 

FROM statistics_record_perhour a






		ORDER BY ${page.orderBy}
