mapper.select:
SELECT 

FROM statistics_trans_record a

WHERE a.id = #{id}

SELECT a.merchant_id AS "merchantId", a.merchant_company AS "merchantCompany", a.trans_type AS "transType", a.product_code AS "productCode", a.pay_type AS "payType", a.allow_system AS "allowSystem", SUM(a.total_count) AS "totalCount", SUM(a.total_amount) AS "totalAmount", SUM(a.sucess_count) AS "sucessCount", SUM(a.sucess_amount) AS "sucessAmount", SUM(a.fee_amount)AS "feeAmount", SUM(a.fail_count) AS "failCount", SUM(a.fail_amount) AS "failAmount"
FROM statistics_trans_record a
AND a.merchant_id = #{merchantId}
AND a.merchant_company LIKE '%'||#{merchantCompany}||'%' '%'+#{merchantCompany}+'%' concat('%',#{merchantCompany},'%')
AND a.allow_system = #{allowSystem}
AND a.trans_type = #{transType}
AND a.product_code = #{productCode}
AND a.pay_type = #{payType}
AND a.statistics_time BETWEEN #{beginStatisticsTime} AND #{endStatisticsTime}
AND a.statistic_hour is NULL
GROUP BY merchant_id ,merchantCompany ,transType,productCode,payType,allowSystem

SELECT 

FROM statistics_trans_record a




		AND a.merchant_id = #{merchantId}


		AND a.merchant_company LIKE 
			'%'||#{merchantCompany}||'%'
			'%'+#{merchantCompany}+'%'
			concat('%',#{merchantCompany},'%')


		AND a.trans_type = #{transType}


		AND a.product_code = #{productCode}


		AND a.pay_type = #{payType}


		AND a.statistics_time BETWEEN #{beginStatisticsTime} AND #{endStatisticsTime}



	ORDER BY statistics_time desc ,statistic_hour desc

SELECT 

FROM statistics_trans_record a






		ORDER BY ${page.orderBy}
