mapper.select:
SELECT 

FROM subscribe_collection_record_detail a

WHERE a.collect_detail_id = #{id}

SELECT 

FROM subscribe_collection_record_detail a




		AND a.subscribe_id = #{subscribeId}


		AND a.collect_detail_id = #{collectDetailId}


		AND a.collect_detail_id = #{collectDetailId}


		AND a.merchant_id = #{merchantId}


		AND a.merchant_user_id = #{merchantUserId}


		AND a.merchant_order_no = #{merchantOrderNo}


		AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d 00:00:00') AND date_format(#{endCreateTime},'%Y-%m-%d 23:59:59')




		ORDER BY ${page.orderBy}

SELECT 

FROM subscribe_collection_record_detail a






		ORDER BY ${page.orderBy}
