mapper.select:
SELECT 

FROM charge_record a


		AND a.merchant_id = #{userId}



			AND a.status ="CAGING"


			AND a.status = #{transStatus}





		AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')




		ORDER BY ${page.orderBy}

SELECT 

FROM withdraw_record a


		AND a.merchant_id = #{merchant_id}



			AND a.status ="DRAING"


			AND a.status = #{status}





		AND a.create_time BETWEEN date_format(#{beginCreateTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCreateTime},'%Y-%m-%d %H:%i:%s')




		ORDER BY ${page.orderBy}
