  	SELECT 

		FROM pbc_account_dynamic a
	WHERE account_dynamic_id = #{accountDynamicId}

  	SELECT 

		FROM pbc_account_dynamic a
	WHERE application_code = #{applicationCode}

  	SELECT 

		FROM pbc_account_dynamic a
