mapper.select:
SELECT 

FROM pbc_bank_info a



		AND a.bank_id = #{bankId} 



		AND a.bank_account = #{bankAccount} 



		AND a.application_code = #{applicationCode} 




		ORDER BY pbc_id DESC,${page.orderBy}


		ORDER BY pbc_id DESC

mapper.insert:
insert into pbc_bank_info (pbc_id,bank_id, bank_name, 
   bank_account, card_type, card_validation, 
   card_expiry_date, card_info, application_code
   )
 values (#{pbcId,jdbcType=BIGINT},#{bankId,jdbcType=VARCHAR}, #{bankName,jdbcType=VARCHAR}, 
   #{bankAccount,jdbcType=VARCHAR}, #{cardType,jdbcType=VARCHAR}, #{cardValidation,jdbcType=VARCHAR}, 
   #{cardExpiryDate,jdbcType=VARCHAR}, #{cardInfo,jdbcType=VARCHAR}, #{applicationCode,jdbcType=VARCHAR}
   )

mapper.delete:
delete from pbc_bank_info
where 1=1


		AND pbc_id = #{pbcId,jdbcType=BIGINT}


		AND application_code = #{applicationCode,jdbcType=VARCHAR}

