mapper.select:
SELECT 

FROM pbc_payment_account




		AND measure_start_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND measure_end_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND payment_account_id = #{paymentAccountId} 



		AND feature_code = #{featureCode} 



		AND account_owner_id = #{accountOwnerId} 



		AND tel_number = #{telNumber} 



		AND login_id = #{loginId} 



		AND wechat_id = #{wechatId} 



		AND qq = #{qq} 



		AND regular_device_number = #{regularDeviceNumber} 



		AND merchant_number = #{merchantNumber} 



		AND pos_identity = #{posIdentity} 



		AND bank_id = #{bankId} 



		AND bank_account = #{bankAccount} 



		AND payment_account_number = #{paymentAccountNumber} 



		AND application_code = #{applicationCode} 




		ORDER BY payment_account_id DESC,${page.orderBy}


		ORDER BY payment_account_id DESC

  	SELECT 

		FROM pbc_payment_account a
	WHERE payment_account_id = #{paymentAccountId}

  	SELECT 

		FROM pbc_payment_account a
	WHERE application_code = #{applicationCode}

  		SELECT 

		FROM pbc_payment_account a
		WHERE application_code=#{applicationCode}
