mapper.select:
SELECT 

FROM pbc_query_info a




		AND application_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND deal_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND trans_serial_number = #{transSerialNumber} 



		AND application_id = #{applicationId} 



		AND case_number = #{caseNumber} 



		AND online_pay_company_id = #{onlinePayCompanyId} 



		AND application_org_id = #{applicationOrgId} 



		AND operator_id_number = #{operatorIdNumber} 



		AND operator_phone_number = #{operatorPhoneNumber} 



		AND investigator_id_number = #{investigatorIdNumber} 



		AND original_application_id = #{investigatorIdNumber} 




		ORDER BY query_info_id DESC,${page.orderBy}


		ORDER BY query_info_id DESC

  	SELECT 

		FROM pbc_query_info a
	WHERE query_info_id = #{queryInfoId}

  	SELECT 

		FROM pbc_query_info a
	WHERE application_id = #{applicationId}
