mapper.select:
SELECT 

FROM pbc_trans_detail_query a




		AND trans_start_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND trans_end_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND data_type = #{dataType} 



		AND payment_account_detail_type = #{paymentAccountDetailType} 



		AND application_code = #{applicationCode} 




		ORDER BY payment_account_trans_detail_id DESC,${page.orderBy}


		ORDER BY payment_account_trans_detail_id DESC

mapper.insert:
insert into pbc_trans_detail_query (payment_account_trans_detail_id, data_type, 
  data, inquiry_mode, subject_type, 
  start_time, expire_time, application_code
  )
values (#{paymentAccountTransDetailId,jdbcType=BIGINT}, #{dataType,jdbcType=VARCHAR}, 
  #{data,jdbcType=VARCHAR}, #{inquiryMode,jdbcType=VARCHAR}, #{subjectType,jdbcType=VARCHAR}, 
  #{startTime,jdbcType=TIMESTAMP}, #{expireTime,jdbcType=TIMESTAMP}, #{applicationCode,jdbcType=VARCHAR}
  )

mapper.select:
  	SELECT 

		FROM pbc_trans_detail_query a
	WHERE payment_account_trans_detail_id = #{paymentAccountTransDetailId}

  	SELECT 

		FROM pbc_trans_detail_query a
	WHERE application_code = #{applicationCode}
