mapper.select:
SELECT 

FROM pbc_trans_info a



		AND a.application_code = #{applicationCode} 




		ORDER BY pbc_id DESC,${page.orderBy}


		ORDER BY pbc_id DESC

mapper.delete:
delete from pbc_trans_info
where 1=1


		AND pbc_id = #{pbcId,jdbcType=BIGINT}


		AND application_code = #{applicationCode,jdbcType=VARCHAR}

mapper.select:
  	SELECT 

		FROM pbc_trans_info a
	WHERE pbc_id = #{pbcId}

  	SELECT 

		FROM pbc_trans_info a
	WHERE application_code = #{applicationCode}
