mapper.select:
SELECT 

FROM pbc_account_detail_query a




		AND trans_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND bank_id = #{bankId} 



		AND application_code = #{applicationCode} 




		ORDER BY trans_serial_card_payment_accoun_id DESC,${page.orderBy}


		ORDER BY trans_serial_card_payment_accoun_id DESC
