mapper.insert:
insert into pcac_merchant_compared_investigation (investigation_id, batch_no, code, 
  name, legal_name, create_time
  )
values (#{investigationId,jdbcType=BIGINT}, #{batchNo,jdbcType=VARCHAR}, #{code,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{legalName,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select investigation_id, batch_no, code, name, legal_name, create_time
from pcac_merchant_compared_investigation
where investigation_id = #{investigationId,jdbcType=BIGINT}

		SELECT 
			investigation_id, batch_no, code, 
             name, legal_name, create_time
		FROM pcac_merchant_compared_investigation a



			      AND a.name = #{name} 




				AND a.create_time BETWEEN date_format(#{beginCheckTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCheckTime},'%Y-%m-%d %H:%i:%s')



				AND a.batch_no = #{batchNo} 




				ORDER BY investigation_id DESC,${page.orderBy}


				ORDER BY investigation_id DESC
