mapper.select:
		SELECT 
			info_id, batch_no, reg_name, cus_name, doc_type, doc_code, doc_name, leg_doc_type, 
            leg_doc_code, level, risk_type, valid_date, valid_status, create_time
		FROM pcac_risk_hint_info a




			      AND a.reg_name = #{regName} 




				AND a.create_time BETWEEN date_format(#{beginCheckTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endCheckTime},'%Y-%m-%d %H:%i:%s')



				AND a.risk_type = #{riskType} 


				AND a.level = #{level} 


				AND a.valid_status = #{validStatus} 


				AND a.leg_doc_type = #{legDocType} 




				ORDER BY info_id DESC,${page.orderBy}


				ORDER BY info_id DESC

SELECT 

FROM pcac_risk_hint_info a



		   and DATE_FORMAT(a.create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginCheckTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endCheckTime}, '%Y-%m-%d')    




	      AND a.reg_name = #{regName} 


		AND a.risk_type = #{riskType} 


		AND a.level = #{level} 


		AND a.valid_status = #{validStatus} 



ORDER BY info_id desc
