mapper.select:
SELECT 

FROM risk_block_info a

WHERE a.block_id = #{id}

SELECT 

FROM risk_block_info a




		AND a.createtime BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND a.block_type = #{blockType}



		AND a.rule_id = #{ruleId}



		AND a.monitor_object = #{monitorObject}



		AND a.buzi_type = #{buziType}




		ORDER BY ${page.orderBy}


		ORDER BY a.createtime DESC
