mapper.select:
SELECT 

FROM risk_channel_order_conversion_ratio a

WHERE a.id = #{id}

SELECT 

FROM risk_channel_order_conversion_ratio a


	1=1

		AND a.channel_partner_code = #{channelPartnerCode}


		AND a.channel_type_code = #{channelTypeCode}


		AND a.host = #{host}


		AND DATE_FORMAT(a.create_time,"%Y-%m-%d") BETWEEN #{beginStatisticTime} AND #{endStatisticTime}




		ORDER BY ${page.orderBy}

SELECT
DATE_FORMAT(create_time,"%H") as channelPartnerCode,
sum(a.channel_success_order)/sum(a.channel_total_order) AS "sucessRatio"
FROM risk_channel_order_conversion_ratio a


	1 = 1 AND a.channel_success_order != 0 AND a.channel_total_order != 0

		AND DATE_FORMAT(a.create_time,"%Y-%m-%d") BETWEEN #{beginStatisticTime} AND #{endStatisticTime}


		AND a.channel_partner_code = #{channelPartnerCode}


		AND a.channel_type_code = #{channelTypeCode}


		AND a.host = #{host}




		ORDER BY ${page.orderBy}




group by DATE_FORMAT(a.create_time,"%H")

SELECT
DATE_FORMAT(create_time,"%Y-%m-%d") as channelPartnerCode,
sum(a.channel_success_order)/sum(a.channel_total_order) AS "sucessRatio"
FROM risk_channel_order_conversion_ratio a


	1 = 1 AND a.channel_success_order != 0 AND a.channel_total_order != 0

		AND DATE_FORMAT(a.create_time,"%Y-%m-%d") BETWEEN #{beginStatisticTime} AND #{endStatisticTime}



		AND a.channel_partner_code = #{channelPartnerCode}


		AND a.channel_type_code = #{channelTypeCode}


		AND a.host = #{host}




		ORDER BY ${page.orderBy}




group by DATE_FORMAT(a.create_time,"%Y-%m-%d")

SELECT
DATE_FORMAT(create_time,"%Y-%m-%d") as channelPartnerCode,
sum(a.channel_success_order)/sum(a.channel_total_order) AS "sucessRatio"
FROM risk_channel_order_conversion_ratio a


	1 = 1 AND a.channel_success_order != 0 AND a.channel_total_order != 0

		AND DATE_FORMAT(a.create_time,"%Y-%m-%d") BETWEEN #{beginStatisticTime} AND #{endStatisticTime}


		AND a.channel_partner_code = #{channelPartnerCode}


		AND a.channel_type_code = #{channelTypeCode}


		AND a.host = #{host}




		ORDER BY ${page.orderBy}




group by DATE_FORMAT(a.create_time,"%Y-%m-%d")

