mapper.select:
SELECT 

FROM risk_internal_monitor_merchant a

WHERE a.internal_merchant_id = #{id}

SELECT 

FROM risk_internal_monitor_merchant a



		AND a.merchant_id = #{merchantId}


		AND a.merchant_name = #{merchantName}


		AND a.product_code = #{productCode}


		AND a.product_name = #{productName}


		AND a.frequency = #{frequency}


		AND a.threshold = #{threshold}



		AND a.create_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d 00:00:00') AND date_format(#{endOperEndTime},'%Y-%m-%d 23:59:59')




		ORDER BY ${page.orderBy}

SELECT 

FROM risk_internal_monitor_merchant a






		ORDER BY ${page.orderBy}
