mapper.select:
SELECT 

FROM risk_merchant_order_conversion_ratio a

WHERE a.id = #{id}

SELECT 

FROM risk_merchant_order_conversion_ratio a


	1=1

		AND a.merchant_id = #{merchantId}


		AND a.product_code = #{productCode}


		AND a.host = #{host}


		AND DATE_FORMAT(a.create_time,"%Y-%m-%d") BETWEEN #{beginStatisticTime} AND #{endStatisticTime}




		ORDER BY ${page.orderBy}

SELECT
	DATE_FORMAT(create_time,"%H") as merchantId,
	sum(a.merchant_sucess_order)/sum(a.merchant_total_order) AS "sucessRatio"
FROM risk_merchant_order_conversion_ratio a


	1 = 1 AND a.merchant_sucess_order != 0 AND a.merchant_total_order != 0

		AND DATE_FORMAT(a.create_time,"%Y-%m-%d") BETWEEN #{beginStatisticTime} AND #{endStatisticTime}


		AND a.merchant_id = #{merchantId}


		AND a.product_code = #{productCode}


		AND a.host = #{host}




		ORDER BY ${page.orderBy}




group by DATE_FORMAT(a.create_time,"%H")

SELECT
DATE_FORMAT(create_time,"%Y-%m-%d") as merchantId,
sum(a.merchant_sucess_order)/sum(a.merchant_total_order) AS "sucessRatio"
