mapper.select:
SELECT 

FROM risk_rulelist a

WHERE a.rulelist_id = #{id}

SELECT 

FROM risk_rulelist a




		AND a.create_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')



		AND a.rule_id = #{ruleId}



		AND a.risk_control_action = #{riskControlAction}



		AND a.rule_type = #{ruleType}



		AND a.monitor_object = #{monitorObject}



		AND a.buzi_type = #{buziType}



		AND a.other_item = #{otherItem}



		AND a.status = #{status}




		ORDER BY ${page.orderBy}

SELECT 

FROM risk_rulelist a






		ORDER BY ${page.orderBy}

SELECT 

FROM risk_rulelist a
WHERE a.rule_id = #{ruleId}
