SELECT

FROM route_pay_channel a

WHERE channel_code = #{channelCode,jdbcType=CHAR}

select DISTINCT t2.`merchant_id` as "merchantId", t3.`bank_no` as "bankNo", t3.`channel_type_code` as "channelTypeCode", t3.`card_type_code` as "cardTypeCode", t3.`channel_partner_code` as "channelPartnerCode", t3.`sort` as "sort", t3.`cost_type` as "costType", t3.`cost_rate` as "costRate", t3.`cost_count` as "costCount", t3.`channel_code` as "channelCode", t3.`account_type` as "accountType", t3.`business_type` as "businessType" from route_product_detail t1 left join merchant_product_rate t2 on t1.`product_code`= t2.`product_code` left join route_pay_channel t3 on t1.`channel_code` = t3.`channel_code` left join product t4 on t1.`product_code` = t4.`code`
AND t3.`bank_no` = #{bankNo,jdbcType=CHAR}
AND t3.`channel_type_code` = #{channelTypeCode,jdbcType=CHAR}
AND t3.`card_type_code` = #{cardTypeCode,jdbcType=CHAR}
AND t3.`account_type` = #{accountType,jdbcType=CHAR}
AND t3.`business_type` = #{businessType,jdbcType=CHAR}
AND t2.`merchant_id` = #{merchantId,jdbcType=CHAR}
AND t3.`status` = 'ENABLE' AND t4.`status` = 'ENABLE' AND now() BETWEEN effect_start_time AND effect_end_time

SELECT

FROM route_pay_channel a



		AND bank_no = #{bankNo,jdbcType=CHAR}


		AND channel_partner_code = #{channelPartnerCode,jdbcType=CHAR}


		AND channel_type_code = #{channelTypeCode,jdbcType=CHAR}


		AND card_type_code = #{cardTypeCode,jdbcType=CHAR}


		AND account_type = #{accountType,jdbcType=CHAR}


		AND business_type = #{businessType,jdbcType=CHAR}

        SELECT 

        FROM route_pay_channel a



                AND a.bank_no = #{bankNo,jdbcType=CHAR}


                AND a.channel_type_code = #{channelTypeCode,jdbcType=CHAR}


				AND a.card_type_code = #{cardTypeCode,jdbcType=CHAR}

            AND a.status = 'ENABLE'
            AND now() BETWEEN a.effect_start_time AND a.effect_end_time

	    select t1.`channel_name` as "channelName", 
	    t1.`id` as "id", 
		t1.`bank_name` as "bankName",
		t1.`sort` as "sort",
		t1.`channel_partner_name` as "channelPartnerName",
		t1.`channel_type_name` as "channelTypeName",
		t1.`card_type_name` as "cardTypeName",
		t2.`id` as "productDetailId"
		from route_pay_channel t1 
		left join route_product_detail t2 on t1.`channel_code` = t2.`channel_code`
        AND t2.`product_code` = #{productCode} 


				AND t1.`bank_no` = #{bankNo,jdbcType=CHAR}


				AND t1.`channel_type_code` = #{channelTypeCode}


				AND t1.`channel_partner_code`  = #{channelPartnerCode}


				AND  t1.`card_type_code`  = #{cardTypeCode}

				AND  t1.`status`  = 'ENABLE'

	    select t1.`channel_name` as "channelName",
	    t1.`id` as "id",
		t1.`bank_name` as "bankName",
		t1.`sort` as "sort",
		t1.`channel_partner_name` as "channelPartnerName",
		t1.`channel_type_name` as "channelTypeName",
		t1.`card_type_name` as "cardTypeName",
		t2.`id` as "merchantPayChannelId"
		from route_pay_channel t1
		left join merchant_pay_channel t2 on t1.`channel_code` = t2.`channel_code`
        AND t2.`merchant_id` = #{merchantId}
		AND t2.`product_code` = #{productCode}


				AND t1.`bank_no` = #{bankNo,jdbcType=CHAR}


				AND t1.`channel_type_code` = #{channelTypeCode}


				AND t1.`channel_partner_code`  = #{channelPartnerCode}


				AND  t1.`card_type_code`  = #{cardTypeCode}

				AND  t1.`status`  = 'ENABLE'

select bank_no as "bankNo", channel_type_code as "channelTypeCode", card_type_code as "cardTypeCode", channel_partner_code as "channelPartnerCode", sort as "sort", cost_type as "costType", cost_rate as "costRate", cost_count as "costCount", channel_code as "channelCode", account_type as "accountType", business_type as "businessType" from route_pay_channel
AND bank_no = #{bankNo}
AND channel_type_code = #{channelTypeCode}
AND card_type_code = #{cardTypeCode}
AND account_type = #{accountType}
AND status = 'ENABLE' AND cost_type = 'RATIOD' AND business_type = 'OWNBAK' AND now() BETWEEN effect_start_time AND effect_end_time
order by bank_no,channel_type_code,card_type_code,account_type,sort is null, sort,cost_rate

select bank_no as "bankNo", channel_type_code as "channelTypeCode", card_type_code as "cardTypeCode", channel_partner_code as "channelPartnerCode", sort as "sort", cost_type as "costType", cost_rate as "costRate", cost_count as "costCount", channel_code as "channelCode", account_type as "accountType", business_type as "businessType" from route_pay_channel
AND bank_no = #{bankNo}
AND channel_type_code = #{channelTypeCode}
AND card_type_code = #{cardTypeCode}
AND account_type = #{accountType}
AND status = 'ENABLE' AND cost_type = 'COUNTD' AND business_type = 'OWNBAK' AND now() BETWEEN effect_start_time AND effect_end_time
order by bank_no,channel_type_code,card_type_code,account_type,sort is null, sort,cost_count

select bank_no as "bankNo", channel_type_code as "channelTypeCode", card_type_code as "cardTypeCode", channel_partner_code as "channelPartnerCode", sort as "sort", cost_type as "costType", cost_rate as "costRate", cost_count as "costCount", channel_code as "channelCode", account_type as "accountType", business_type as "businessType" from route_pay_channel
AND channel_type_code = #{channelTypeCode}
AND card_type_code = #{cardTypeCode}
AND account_type = #{accountType}
AND status = 'ENABLE' AND business_type = 'SPNBAK' AND cost_type = 'RATIOD' AND now() BETWEEN effect_start_time AND effect_end_time
order by channel_type_code,card_type_code,account_type,sort is null, sort,cost_rate

select bank_no as "bankNo", channel_type_code as "channelTypeCode", card_type_code as "cardTypeCode", channel_partner_code as "channelPartnerCode", sort as "sort", cost_type as "costType", cost_rate as "costRate", cost_count as "costCount", channel_code as "channelCode", account_type as "accountType", business_type as "businessType" from route_pay_channel
AND channel_type_code = #{channelTypeCode}
AND card_type_code = #{cardTypeCode}
AND account_type = #{accountType}
AND status = 'ENABLE' AND business_type = 'SPNBAK' AND cost_type = 'COUNTD' AND now() BETWEEN effect_start_time AND effect_end_time
order by channel_type_code,card_type_code,account_type,sort is null, sort,cost_count

select

from route_pay_channel a
left join merchant_pay_channel b on a.`channel_code` = b.`channel_code`


		AND a.`bank_no` = #{bankNo,jdbcType=CHAR}


		AND a.`channel_type_code` = #{channelTypeCode}


		AND b.`merchant_id`  = #{merchantId}


		AND b.`product_code`  = #{productCode}


		AND  a.`card_type_code`  = #{cardTypeCode}

	AND  a.`status`  = 'ENABLE'
	AND now() BETWEEN a.effect_start_time AND a.effect_end_time

SELECT
count(1)
FROM route_pay_channel a


		AND id != #{id,jdbcType=INTEGER}


		AND bank_no = #{bankNo,jdbcType=CHAR}


