mapper.select:
SELECT 

FROM route_product_detail a

WHERE a.id = #{id}

SELECT 

FROM route_product_detail a



		AND product_code = #{productCode,jdbcType=CHAR}


		AND bank_no = #{bankNo,jdbcType=CHAR}


		AND channel_partner_code = #{channelPartnerCode,jdbcType=CHAR}


		AND channel_type_code = #{channelTypeCode,jdbcType=CHAR}


		AND card_type_code = #{cardTypeCode,jdbcType=CHAR}







		ORDER BY ${page.orderBy}

SELECT 

FROM route_product_detail a






		ORDER BY ${page.orderBy}

SELECT

FROM route_product_detail a

WHERE product_code = #{productCode,jdbcType=CHAR}

SELECT 
    a.bank_name AS "bankName",
    a.bank_no AS "bankNo",
    a.card_type_code AS "cardTypeCode"
FROM route_product_detail a

     AND a.product_code = #{productCode} 

         AND a.card_type_code = #{cardTypeCode} 


         AND a.bank_no = #{bankNo} 



GROUP BY  a.bank_name , a.bank_no , a.card_type_code
