mapper.select:
SELECT 

FROM clearing_channel_record_his a



		   and DATE_FORMAT(a.channel_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginChannelTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.channel_time, '%Y-%m-%d') <= DATE_FORMAT(#{endChannelTime}, '%Y-%m-%d')    



		AND a.trans_type = #{transType}



		AND a.channel_name LIKE #{channelName}



		AND a.channel_type = #{channelType}



		AND a.payment_id = #{paymentId}



		AND a.check_status = #{checkStatus}



		AND a.settle_status = #{settleStatus}



		AND a.channel_code = #{channelCode}





				AND a.check_flg = #{checkFlg}


				AND a.check_flg in (SELECT check_flg FROM clearing_channel_record_his where check_flg is not null and check_flg != 'QSTS')





		AND a.trans_no = #{transNo}



		AND a.settle_bath = #{settleBath}




		ORDER BY ${page.orderBy}


		ORDER BY his_id desc

SELECT 

FROM clearing_channel_record_his a



		   and DATE_FORMAT(a.channel_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginChannelTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.channel_time, '%Y-%m-%d') <= DATE_FORMAT(#{endChannelTime}, '%Y-%m-%d')    



		AND a.payment_id = #{paymentId}



		AND a.trans_type = #{transType}



		AND a.channel_name LIKE #{channelName}



		AND a.channel_type = #{channelType}



		AND a.trans_no = #{transNo}



		AND a.check_status = #{checkStatus}



		AND a.settle_status = #{settleStatus}



		AND a.channel_code = #{channelCode}





				AND a.check_flg = #{checkFlg}


				AND a.check_flg in (SELECT check_flg FROM clearing_channel_record where check_flg is not null and check_flg != 'QSTS')





		AND a.settle_bath = #{settleBath}


ORDER BY his_id desc
