mapper.select:
SELECT 

FROM settle_merchant_record_his a



		   and DATE_FORMAT(a.check_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginCheckTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.check_time, '%Y-%m-%d') <= DATE_FORMAT(#{endCheckTime}, '%Y-%m-%d')    




		   and DATE_FORMAT(a.settle_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginSettleTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.settle_time, '%Y-%m-%d') <= DATE_FORMAT(#{endSettleTime}, '%Y-%m-%d')    




		AND a.trans_type = #{transType}



		AND a.merchant_id = #{merchantId}



		AND a.settle_status = #{settleStatus}



		AND a.settle_bath = #{settleBath}




		ORDER BY ${page.orderBy}


		ORDER BY his_id desc

SELECT 

FROM settle_merchant_record_his a



		   and DATE_FORMAT(a.check_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginCheckTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.check_time, '%Y-%m-%d') <= DATE_FORMAT(#{endCheckTime}, '%Y-%m-%d')    




		   and DATE_FORMAT(a.settle_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginSettleTime}, '%Y-%m-%d')   


		  and DATE_FORMAT(a.settle_time, '%Y-%m-%d') <= DATE_FORMAT(#{endSettleTime}, '%Y-%m-%d')    




		AND a.trans_type = #{transType}



		AND a.merchant_id = #{merchantId}



		AND a.settle_status = #{settleStatus}



		AND a.settle_bath = #{settleBath}


ORDER BY a.his_id desc
