mapper.select:
SELECT 

FROM tpds_cut_day



		AND create_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')




		AND busi_no = #{busiNo} 




		AND cut_type = #{cutType} 





		ORDER BY tpds_cut_day_id DESC,${page.orderBy}


		ORDER BY tpds_cut_day_id DESC
