mapper.select:
SELECT 

FROM tpds_merchant



		AND create_time BETWEEN date_format(#{beginOperEndTime},'%Y-%m-%d %H:%i:%s') AND date_format(#{endOperEndTime},'%Y-%m-%d %H:%i:%s')




		AND merchant_no = #{merchantNo} 




		AND company_name = #{companyName} 





		ORDER BY merchant_id DESC,${page.orderBy}


		ORDER BY merchant_id DESC

select 

from tpds_merchant


    AND merchant_id = #{merchantId}


    AND login_name = #{loginName}


    AND merchant_no = #{merchantNo}


    AND email = #{email,jdbcType=VARCHAR}


    AND contry_name = #{contryName,jdbcType=VARCHAR}


    AND type = #{type,jdbcType=CHAR}


    AND certificate_type = #{certificateType,jdbcType=CHAR}


    AND company_name = #{companyName,jdbcType=VARCHAR}


    AND province_code = #{provinceCode,jdbcType=CHAR}


    AND province_name = #{provinceName,jdbcType=VARCHAR}


    AND city_code = #{cityCode,jdbcType=VARCHAR}


    AND city_name = #{cityName,jdbcType=VARCHAR},


    AND county_code = #{countyCode,jdbcType=CHAR}


    AND county_name = #{countyName,jdbcType=VARCHAR}


    AND business_address = #{businessAddress,jdbcType=VARCHAR}


    AND website = #{website,jdbcType=VARCHAR}


    AND business_license_no = #{businessLicenseNo,jdbcType=VARCHAR}


    AND business_license_end_time = #{businessLicenseEndTime,jdbcType=TIMESTAMP}


    AND organization_code = #{organizationCode,jdbcType=VARCHAR}


    AND tax_registration_certificate_no = #{taxRegistrationCertificateNo,jdbcType=VARCHAR}


    AND business_scope = #{businessScope,jdbcType=VARCHAR},


    AND llegalperson = #{llegalperson,jdbcType=VARCHAR}


    AND legal_idcard = #{legalIdcard,jdbcType=VARCHAR}


    AND legal_certificate_end_time = #{legalCertificateEndTime,jdbcType=TIMESTAMP}


    AND contactor = #{contactor,jdbcType=VARCHAR}


    AND contactor_idcard_no = #{contactorIdcardNo,jdbcType=VARCHAR}


    AND contactor_certificate_end_time = #{contactorCertificateEndTime,jdbcType=TIMESTAMP}


    AND contactor_phone = #{contactorPhone,jdbcType=VARCHAR}


    AND supplier_name = #{supplierName,jdbcType=VARCHAR}


    AND enterprise_name = #{enterpriseName,jdbcType=VARCHAR}


    AND electricity_url = #{electricityUrl,jdbcType=VARCHAR}


    AND individual_user = #{individualUser,jdbcType=VARCHAR}


    AND contry_code = #{contryCode,jdbcType=VARCHAR}


    AND merchant_pos_code = #{merchantPosCode,jdbcType=CHAR}


    AND company_phone = #{companyPhone,jdbcType=VARCHAR}


    AND legal_mobile = #{legalMobile,jdbcType=VARCHAR}


    AND charge_type = #{chargeType,jdbcType=TINYINT}


    AND ipc_no = #{ipcNo,jdbcType=VARCHAR}


    AND permits_accounts = #{permitsAccounts,jdbcType=VARCHAR}


    AND legal_certificate_front = #{legalCertificateFront,jdbcType=VARCHAR}


    AND legal_certificate_back = #{legalCertificateBack,jdbcType=VARCHAR}


    AND tax_registration_certificate = #{taxRegistrationCertificate,jdbcType=VARCHAR}


    AND organization_code_certificate = #{organizationCodeCertificate,jdbcType=VARCHAR}


    AND business_license_file = #{businessLicenseFile,jdbcType=VARCHAR}


    AND trademark_regis_photo_address = #{trademarkRegisPhotoAddress}



    AND create_time = #createTime}


    AND create_author = #{createAuthor}


    AND update_time = #{updateTime}


    AND update_author = #{updateAuthor}
