mapper.select:
SELECT 

FROM tpds_product_key




		   and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s')>=  DATE_FORMAT(#{beginOperEndTime}, '%Y-%m-%d %H:%i:%s')   
		  and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endOperEndTime}, '%Y-%m-%d %H:%i:%s')    




		AND merchant_no = #{merchantNo} 




		AND trans_type = #{transType} 




		AND product_code = #{productCode} 





		ORDER BY key_id DESC,${page.orderBy}


		ORDER BY key_id DESC
