mapper.select:
SELECT 

FROM trial_balance_trans a

WHERE a.id = #{id}

SELECT 

FROM trial_balance_trans a



		AND a.trans_no = #{transNo}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


	    ORDER BY create_time DESC

mapper.update:
UPDATE trial_balance_trans SET 	
	status = 'FINISH'			
WHERE id = #{id}

UPDATE trial_balance_trans SET 	
	status = #{status},
	update_time = #{updateTime}			
WHERE id = #{id}
