mapper.select:
    select rulelist_id, rule_type, monitor_object, buzi_type, rule_id, rule_description, 
    risk_control_action, risk_control_fact, other_item, create_time,status ,operator
    from risk_rulelist 


				AND rulelist_id = #{rulelistId,jdbcType=INTEGER}


				AND status = #{status}


				AND rule_id = #{ruleId}


				AND monitor_object = #{monitorObject}
