mapper.select:
	select

  from tpds_settle_item
  where merchant_no = #{merchantNo,jdbcType=VARCHAR} and trans_type = #{transType,jdbcType=VARCHAR}
  and success_time <= #{successTime} and settle_status = #{settleStatus,jdbcType=VARCHAR}

mapper.update:
	update tpds_settle_item
  set settle_status = #{settleStatus,jdbcType=VARCHAR},finish_time = #{finishTime,jdbcType=TIMESTAMP}
  where trans_no = #{transNo,jdbcType=VARCHAR}
